public class LeetCode0076 {
    public static void main(String[] args) {
        String s = "obnbtoeatga";
        String t = "nb";
        System.out.println(minWindow(s, t));
    }

    public static String minWindow(String s, String t) {
        int length1 = s.length();
        int length2 = t.length();
        int minLength = length2;
        String temp = t;

        int count = 0;
        while (minLength <= length1) {
            for (int i = 0; i < length1 - minLength + 1; i++) {
                for (int j = i; j < minLength + i; j++) {
                    for (int k = 0; k < temp.length(); k++) {
                        if (s.charAt(j) == temp.charAt(k)) {
                            count++;
                            //k--;
                            temp = temp.substring(0, k) + temp.substring(k + 1);
                            break;
                        }
                    }
                }
                if (count == length2) {
                    return s.substring(i, i + minLength);
                } else {
                    temp = t;
                    count = 0;
                }
            }
            minLength++;
        }


        return "";
    }
}

